async function PathExists(n,t){var i=new XMLHttpRequest;i.responseType="blob";let r="https://localhost:7242/Home/Download?path="+n+"&type="+t;i.open("GET",r,!0);i.onreadystatechange=function(){this.response!=null&&(this.status==200?saveBlob(i.response,"FileData."+t):alert("Item Is Not Available"))};i.send()}function saveBlob(n,t){var i=document.createElement("a");i.href=window.URL.createObjectURL(n);i.download=t;i.dispatchEvent(new MouseEvent("click"))}function deleteConfirm(n){let t="https://localhost:7242/Home/Delete?path="+n;sweetAlert({title:"Are you sure?",text:"Once deleted, you will not be able to recover\n",type:"warning",showConfirmButton:!0,showCancelButton:!0,confirmButtonText:"Yes, Delete It",cancelButtonText:"No"}).then(async function(n){if(n.dismiss!="cancel"){const n=await fetch(t,{method:"GET"});n.ok?sweetAlert({title:"Done!",text:"File Deleted Successfully",type:"success"}):sweetAlert("Error Occured","File Didn't Exist Or An Error Occurred","error")}else sweetAlert("Proccess Canceled ","File Is Safe","success")})}document.getElementById("selectfilehomepageinput").onchange=function(){document.getElementById("selectfilehomepagelabel").innerHTML="Selected";document.getElementById("selectfilehomepagesubmit").type="submit"};const alldownloadbuttons=document.querySelectorAll(".home-index");for(const n of alldownloadbuttons)n.addEventListener("click",function(){PathExists(n.getAttribute("data-path"),n.getAttribute("data-type"))});const allDeleteButtons=document.querySelectorAll(".home-index-delete");for(const n of allDeleteButtons)n.addEventListener("click",function(){deleteConfirm(n.getAttribute("data-path"))});